mapper.select:
/* 컨텐츠 정보 */ SELECT c.CONTENTS_ID AS contentsId, c.CONTENTS_NAME AS contentsName, IFNULL(CONCAT(c.CONTENTS_SELL_START_DATE, '~', c.CONTENTS_SELL_END_DATE), 0) AS contentsSellDuration, IF(si.STORE_CATEGORY_NAME = '도서', 'book', IF(si.STORE_CATEGORY_NAME = '체육', 'exercise', IF(c.CONTENTS_PG=0, '전체 관람가', CONCAT(c.CONTENTS_PG, '세 이상 관람가')))) AS contentsPg, CONCAT(FORMAT(c.CONTENTS_PRICE, 0), '원') AS contentsPrice,
si.STORE_NAME AS storeName, c.AMOUNT_CONTENT_REMAINING AS amountContentsRemaining FROM CONTENTS AS c INNER JOIN STORE_INFO AS si ON si.STORE_ID=c.STORE_ID ORDER BY c.CONTENTS_REG_DATE
LIMIT #{startContentsNum}, #{contentsPerPage};

/* 탭 컨텐츠 정보 */ SELECT c.CONTENTS_ID AS contentsId, c.CONTENTS_NAME AS contentsName, c.CONTENTS_SELL_START_DATE AS contentsSellStartDate, c.CONTENTS_SELL_END_DATE AS contentsSellEndDate, si.STORE_CATEGORY_NAME AS contentsCategoryName, c.CONTENTS_PG AS contentsPg, CONCAT(FORMAT(c.CONTENTS_PRICE, 0), '원') AS contentsPrice,
si.STORE_NAME AS storeName, c.AMOUNT_CONTENT_REMAINING AS amountContentRemaining FROM CONTENTS AS c INNER JOIN STORE_INFO AS si ON si.STORE_ID=c.STORE_ID
c.CONTENTS_CAT_CODE LIKE CONCAT(#{tabValue}, '__')
ORDER BY c.CONTENTS_REG_DATE
LIMIT #{startContentsNum}, #{contentsPerPage};

/* 탭과 검색 컨텐츠 정보 */ SELECT c.CONTENTS_ID AS contentsId, c.CONTENTS_NAME AS contentsName, IFNULL(CONCAT(c.CONTENTS_SELL_START_DATE, '~', c.CONTENTS_END_DATE), 0) AS contentsSellDuration, IF(si.STORE_CATEGORY_NAME = '도서', 'book', IF(si.STORE_CATEGORY_NAME = '체육', 'exercise', IF(c.CONTENTS_PG=0, '전체 관람가', CONCAT(c.CONTENTS_PG, '세 이상 관람가')))) AS contentsPg, CONCAT(FORMAT(c.CONTENTS_PRICE, 0), '원') AS contentsPrice,
si.STORE_NAME AS storeName, c.AMOUNT_CONTENT_REMAINING AS amountContentRemaining FROM CONTENTS AS c INNER JOIN STORE_INFO AS si ON si.STORE_ID=c.STORE_ID
c.CONTENTS_CAT_CODE LIKE CONCAT(#{tabValue}, '__')
AND c.CONTENTS_CAT_CODE = #{performanceGenre}
ORDER BY c.CONTENTS_REG_DATE
LIMIT #{startContentsNum}, #{contentsPerPage};

/* 각 탭의 컨텐츠 개수 */
SELECT
COUNT(1)
FROM
CONTENTS AS c


        c.CONTENTS_CAT_CODE LIKE CONCAT(#{tabValue}, '__');

/* 각 탭의 서칭 결과 컨텐츠 개수 */
SELECT
COUNT(1)
FROM
CONTENTS AS c


        c.CONTENTS_CAT_CODE LIKE CONCAT(#{tabValue}, '__');


        AND c.CONTENTS_CAT_CODE = #{performanceGenre}

mapper.insert:
/* 컨텐츠아이디 자동증가 */ SELECT (CASE WHEN COUNT(c.CONTENTS_ID) = 0 THEN CONCAT('CNT', DATE_FORMAT(CURDATE(), '%Y%m%d'), '0001') WHEN (MAX(CAST(SUBSTRING(c.CONTENTS_ID, 12) AS UNSIGNED))+1) > 999 THEN CONCAT(CONCAT('CNT', DATE_FORMAT(CURDATE(), '%Y%m%d')), MAX(CAST(SUBSTRING(CONTENTS_ID, 12) AS UNSIGNED))+1) ELSE CONCAT(CONCAT('CNT', DATE_FORMAT(CURDATE(), '%Y%m%d')), LPAD(MAX(CAST(SUBSTRING(CONTENTS_ID, 12) AS UNSIGNED))+1, 4, '0')) END) AS contentsId FROM CONTENTS AS c WHERE c.CONTENTS_ID LIKE CONCAT('CNT', DATE_FORMAT(CURDATE(), '%Y%m%d'), '%');
INSERT INTO CONTENTS(CONTENTS_ID, CONTENTS_CAT_CODE, REGION_CODE, STORE_ID, CONTENTS_CAT_NAME, CONTENTS_NAME, CONTENT_ADDRESS, CONTENTS_DURATION, CONTENTS_PG, CONTENTS_PRICE, CONTENTS_RELEASE_DT, CONTENTS_REG_DATE, CONTENTS_SELL_START_DATE, CONTENTS_SELL_END_DATE, AMOUNT_CONTENT_REGISTERED, AMOUNT_CONTENT_REMAINING, CONTENTS_DETAIL)
VALUES(#{contentsId}, #{contentsCategoryCode}, #{regionCode}, #{storeId}, #{contentsCategoryName}, #{contentsName}, #{contentsAddress}, #{contentsDuration}, #{contentsPg}, #{contentsPrice}, #{contentsReleaseDT}, CURDATE(), #{contentsSellStartDate}, #{contentsSellEndDate}, #{amountContentRegistered}, #{amountContentRemaining}, #{contentsDetail})
