mapper.select:
SELECT p.PAYMENT_CODE, p.USER_ID, p.CONTENTS_ID, c.CONTENTS_NAME, c.CONTENTS_CAT_NAME, c.CONTENT_ADDRESS, p.MUNHWACARD_NUM, p.RESERVATION_CODE, p.DELIVERY_COSTS, p.CONTENTS_PRICE, p.PAYMENT_QUANTITY, p.PAYMENT_AMOUNT, p.PAYMENT_STATUS, p.PAYMENT_DT, p.IS_REFUND, p.IS_CLOSING, p.CLOSING_CODE FROM PAYMENT AS p INNER JOIN USER_INFO AS u ON u.USER_ID = p.USER_ID
INNER JOIN MUNHWACARD AS m ON m.MUNHWACARD_NUM = p.MUNHWACARD_NUM
INNER JOIN CONTENTS AS c ON c.CONTENTS_ID = p.CONTENTS_ID WHERE u.USER_ID = #{userId}

SELECT p.PAYMENT_CODE, p.USER_ID, p.CONTENTS_ID, c.CONTENTS_NAME, c.CONTENTS_CAT_NAME, c.CONTENT_ADDRESS, p.MUNHWACARD_NUM, p.RESERVATION_CODE, p.DELIVERY_COSTS, p.CONTENTS_PRICE, p.PAYMENT_QUANTITY, p.PAYMENT_AMOUNT, p.PAYMENT_STATUS, p.PAYMENT_DT, p.IS_REFUND, p.IS_CLOSING, p.CLOSING_CODE FROM PAYMENT AS p INNER JOIN USER_INFO AS u ON u.USER_ID = p.USER_ID INNER JOIN MUNHWACARD AS m ON m.MUNHWACARD_NUM = p.MUNHWACARD_NUM INNER JOIN CONTENTS AS c ON c.CONTENTS_ID = p.CONTENTS_ID
PAYMENT_DT >= #{item.searchValue}
PAYMENT_DT <= #{item.searchValue}
${item.searchKey} = #{item.searchValue}
